mapper.update:
update class set reqState = 'C', rejectMsg = #{rejectMsg}
where reqState in('AR','A') and classSeq in
#{i.classSeq}

mapper.delete:
delete from stdTimeTable where
	classCode in#{i.classCode} 
	and sCode in#{i.sCode}

delete from studentClass where 
classCode in#{i.classCode} 
and sCode in#{i.sCode}
