mapper.select:
select * from (select notice.*, row_number() over(order by
writedate desc)
rn from notice where deptcode ='A') where rn between
#{startRowNum}
and #{endRowNum}

select * from (select notice.*, row_number() over(order by writedate desc) rn from notice where deptcode ='A'
and title like '%'||#{content}||'%'
and contents like '%'||#{content}||'%'
and contents like '%'||#{content}||'%' or deptcode ='A'and title like '%'||#{content}||'%'
) where rn between #{startRowNum} and #{endRowNum}

select * from (select notice.*, row_number() over(order by
writedate desc)
rn from notice where deptcode ='B') where rn between
#{startRowNum}
and #{endRowNum}

